mapper.update:
UPDATE EQM_LINE

	tot_prc = #{totPrc},
	uph = #{uph},
	emp_no = #{empNo},
	line_yn = #{lineYn}

WHERE line_no = #{lineNo}

UPDATE EQM_CHK
chk_date = #{chkDate}, chk_ndate = #{chkNdate}, chk_state = #{chkState}, chk_sol = #{chkSol}, chk_emp_no = #{chkEmpNo}, eqm_code = #{eqmCode}
WHERE chk_no = #{chkNo}

UPDATE EQM_UOPER
uoper_rsn = #{uoperRsn}, uoper_work = #{uoperWork}, uoper_start_time = #{uoperStartTime}, uoper_end_time = #{uoperEndTime}, eqm_code = #{empCode}, emp_id = #{empId}
WHERE uoper_code = #{uoperCode}
