mapper.select:
SELECT b.mat_ord_no, b.mat_ord_qty, m.mat_code, m.mat_name,TO_DATE(b.mat_ord_date,'rrrr/MM/dd') as MAT_ORD_DATE, c.cp_code, c.cp_name, b.mat_price, b.mat_ord_emp, b.mat_ord_yn, e.emp_id FROM mat_buy b JOIN material m ON m.mat_code = b.mat_code JOIN company c ON c.cp_code = m.cp_code JOIN employee e on e.emp_name = b.mat_ord_emp
TRUNC(OD_DATE) = #{startDate}
and b.mat_code = #{matCode}
and c.cp_code = #{cpCode}
order by mat_ord_no DESC

mapper.insert:

	SELECT 'MAT_ORD_'||LPAD(NVL(MAX(SUBSTR(mat_ord_no,
	-3)),0)+1,3,0) FROM
	mat_buy

INSERT INTO mat_buy
VALUES (
#{matOrdNo}
,#{matOrdQty}
,'PRG_001'
,#{matCode}
,sysdate
,#{cpCode}
,'미확인'
,#{matPrice}
,#{matOrdEmp}
)

mapper.update:
UPDATE mat_chk


		MAT_IN_QTY = #{matInQty},


		MAT_CODE = #{matCode}


WHERE MAT_ORD_NO = #{matOrdNo}

mapper.insert:

	SELECT 'LOT_'||TO_CHAR(SYSDATE,'YYYYMMDD')||'_'||LPAD(NVL(MAX(SUBSTR(lot_no, -3)),0)+1,3,0) 
	FROM	lot

INSERT INTO lot	(lot_no
				,lot_date
				,mp_code
				,lot_qty
				,wh_code
				,mat_chk_code)
		VALUES
				(#{lotNo}
				,#{matChkDate}
				,#{matCode}
				,#{lotQty}
				,#{whCode}
				,#{matChkCode})

mapper.update:
UPDATE mat_chk


		mat_err_qty = #{matErrQty},


		mat_pass_qty = #{matPassQty}

	where mat_ord_no = #{matOrdNo}

