mapper.select:
SELECT 'PRE' || TO_CHAR(SYSDATE,'YYYYMMDD')||LPAD(NVL(MAX(SUBSTR(pre_no, -3)),0)+1,3,0) AS preNo
FROM produce
WHERE pre_no = #{preNO}
